			&:not(:last-child) {
				margin-right: 16px;
			}

			&.control:not(:last-child) {
				margin-right: 8px;
			}

			& a {
				display: block;
				transition: color 0.15s ease-in-out;
				color: var(--grey-400);

				&:hover {
					color: var(--grey-300);
				}
			}

			& button,
			a {
				padding: 2px;
			}
		}
	}

	.notifications-btn {
		position: relative;
		color: var(--grey-400);
	}

	.notifications-btn.has-unseen {
		color: var(--grey-100);
	}

	.notifications-count {
		display: none;
		position: absolute;
		background-color: var(--primary-600);
		border: 2px solid var(--grey-700);
		border-radius: 50%;
		padding: 2px;
		height: 12px;
		width: 12px;
		right: -3px;
		top: -3px;
